mapper.insert:
INSERT INTO empl_info (student_id, company_name, company_type, company_province,
                       company_city, sign_date, postgraduate, empl_ontime, empl_withintwo)
SELECT #{studentId}, #{companyName}, #{companyType}, #{companyProvince},
       #{companyCity}, #{signDate}, #{postgraduate}, #{emplOntime}, #{emplWithintwo}
FROM dual
WHERE NOT EXISTS (
    SELECT 1
    FROM empl_info
    WHERE student_id = #{studentId}
)

mapper.delete:
DELETE FROM empl_info
WHERE student_id IN

    #{id}
